ansible.map:
function(doc){
  // 根据名字找到service
  if(doc.type == "service"){
    var name;
    if(doc.name){
      name = doc.name
    }else{
      name = require('views/lib/utils').get(doc.port);
    }
    emit(name,{ip:doc.ip, port:doc.port});
  }
}

