#!/usr/bin/python# -*- coding: utf-8 -*-from __future__ import absolute_importfrom __future__ import unicode_literalsfrom tornado import httpserverfrom tornado import ioloopfrom tornado import webfrom tornado.options import define, optionsclass Application(web.Application):    def __init__(self):        from url import handlers        settings = dict(            debug=True,            # template_path=os.path.join(os.path.dirname(__file__), "templates"),            # static_path=os.path.join(os.path.dirname(__file__), "static"),            # cookie_secret=options.cookie_secret,            # login_url=options.login_url,            # static_url_prefix=options.static_url_prefix,        )        super(Application, self).__init__(handlers, **settings)if __name__ == '__main__':    define("port", default=8005, help='run on the given port', type=int)    options.parse_command_line()    http_server = httpserver.HTTPServer(Application(), xheaders=True)    http_server.listen(options.port)    ioloop.IOLoop.instance().start()